import React from "react";
import NewsCard from "../NewsCard/NewsCard";
import NotFound from "../NotFound/NotFound";
import Preloader from "../Preloader/Preloader";
import { useLocation, useHistory } from "react-router-dom";

function NewsCardList(props) {
  const cards = props.newsCards;
  let location = useLocation();
  const [showItems, setshowItems] = React.useState(3);

  function handleShowMeMore() {
    setshowItems(showItems + 3);
  }

  return (
    <section
      className={`news-cardlist ${
        props.isSearchResult && "news-cardlist_state_enabled"
      }`}
    >
      <h2
        className={`news-cardlist__title ${
          location.pathname === "/" && "news-cardlist__title_state_enabled"
        }`}
      >
        Результаты поиска
      </h2>
      <div className="news-cardlist__gallery">
        {props.totalResult === 0 ? (
          <NotFound />
        ) : (
          cards
            .map((item, id) => {
              return <NewsCard newsCard={item} key={id} />;
            })
            .slice(0, showItems)
        )}

        {/* <button type="button" className="news-cardlist__button-show-me-more" onClick={handleShowMeMore}>Показать еще</button> */}
      </div>
      <div className="news-cardlist__button-wrapper">
        <button
          type="button"
          className="news-cardlist__button-show-me-more"
          onClick={handleShowMeMore}
        >
          Показать еще
        </button>
      </div>
    </section>
  );
}

export default NewsCardList;
