import React from 'react';
import PopupWithForm from './PopupWithForm/PopupWithForm';

function Login(props) {
    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");
    const [errMessageEmail, setErrMessageEmail] = React.useState("");
    const [errMessagePassword, setErrMessagePassword] = React.useState("");

    const resetForm = () => {
        setEmail("");
        setPassword("");
        setErrMessageEmail("");
        setErrMessagePassword("");
      };

      const handleSubmit = (evt) => {
        evt.preventDefault();
        if (!email || '') {
            setErrMessageEmail("Неправильный формат email");
          return;
        }
        // if (!password) {
        //   setErrMessagePassword("Пароль введен неверно");
        //   return;
        // }
        props.onLogin(email, password);
        resetForm();
      };

      const handleChangeEmail = (evt) => {
        setEmail(evt.target.value);
      };

      const handleChangePassword = (evt) => {
        setPassword(evt.target.value);
      };

    return (
        <PopupWithForm id='popup-auth' name='popup_auth' title='Вход' isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit} >
            <fieldset className="popup__form-auth">
                <input id="author-input" type="email" value={email || ''} onChange={handleChangeEmail} name="email" required placeholder="Введите почту" className="popup__input" />
                <span id="author-input-error" className="popup__error_visible">{errMessageEmail}</span>
                <input id="metier-input" type="password" value={password || ''} onChange={handleChangePassword} name="password" minLength="6" maxLength="25" required placeholder="Введите пароль" className="popup__input" />
                <span id="metier-input-error" className="popup__error_visible">{errMessagePassword}</span>
            </fieldset>
            <span id="metier-input-error" className="popup__error_visible">{props.errorLoginMessage}</span>
            <button type="submit" className="popup__button-save">Сохранить</button>
            <div className="popup__redirect">
                <p className="popup__redirect-paragraph">или</p>
                <p className="popup__redirect-link">Войти</p>
            </div>
        </PopupWithForm>
    )
}

export default Login;
