import React from "react";
import { useLocation, useHistory } from "react-router-dom";
import Navigation from "../Navigation/Navigation";
import PopupMenu from "../PopupMenu/PopupMenu";

function Header(props) {
  let location = useLocation();
  const history = useHistory();
  const turnToMain = () => {
    history.push("/");
  };
  return (
    <header
      className={`header ${
        location.pathname === "/saved-news" && "header_status_account"
      }`}
    >
      <div class="header__components">
        <h1 className="header__title" onClick={turnToMain}>
          NewsExplorer
        </h1>
        <Navigation loggedIn={props.loggedIn} onOpenLogin={props.onOpenLogin} onOpenPopupInfo={props.onOpenPopupInfo}/>
        <PopupMenu isOpenPopupMenu={props.isOpenPopupMenu} onClose={props.onClose} />
      </div>
    </header>
  );
}

export default Header;
