import React from "react";
import { useLocation, useHistory } from "react-router-dom";

function Navigation(props) {
  let location = useLocation();
  const history = useHistory();
  const turnToMain = () => {
    history.push("/");
  };
  const turnToSavedNews = () => {
    history.push("/saved-news");
  };
  const signinButton = () => {
    history.push("/signin");
  };

  return (
      <nav className="nav">
        <ui className="nav__links">
          <li onClick={turnToMain} className="nav__link">
            Главная
          </li>
          <li
            onClick={turnToSavedNews}
            className={`${
              props.loggedIn ? "nav__link" : "nav__link_status_disable"
            }`}
          >
            Сохраненные статьи
          </li>
          <button className="nav__log-button" onClick={signinButton}>
            {" "}
            Авторизоваться{" "}
          </button>
        </ui>
      </nav>
  );
}

export default Navigation;
