import React from "react";

import { Router, Route, Switch, Redirect, useHistory } from "react-router-dom";

// import logo from '../../logo.svg';
import "./App.css";
import Header from "../Header/Header";
import Main from "../Main/Main";
import Footer from "../Footer/Footer";
import SavedNews from "../SavedNews/SavedNews";

import NewsApi from "../../utils/NewsApi";

import { url, update, size, apiKey } from "../../utils/Utils";

function App() {
  const history = useHistory();
  const [loggedIn, setLoggedIn] = React.useState(true);
  const [newsCards, setNewsCards] = React.useState({});

  // const onSearchSubmit = () => {
  //   const newsApi = new NewsApi(url, keyword, update, size, apiKey);
  //   newsApi
  //     .getNewsCards()
  //     .then((res) => {
  //       setNewsCards(res);
  //     })
  //     .catch((err) => {
  //       console.log(`Ошибка получения данных о пользователе... ${err}`);
  //     });
  // };

  function onUpdateKeyword(keyword) {
    const newsApi = new NewsApi(url, keyword, update, size, apiKey);
    newsApi
      .getNewsCards()
      .then((res) => {
        setNewsCards(res);
      })
      .catch((err) => {
        console.log(`Ошибка получения данных о пользователе... ${err}`);
      });
  }

  onUpdateKeyword(onUpdateKeyword);

  // React.useEffect(() => {

  //   handleUpdateKeyword(onUpdateKeyword);

  // }, onUpdateKeyword);

  return (
    <Router history={history}>
      <div className="page">
        <Header loggedIn={loggedIn} />

        <Switch>
          <Route exact path="/">
            <Main newsCards={newsCards} onUpdateKeyword={onUpdateKeyword} />
          </Route>

          <Route path="/saved-news">
            <SavedNews />
          </Route>
        </Switch>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
