import React from "react";
import { useLocation, useHistory } from "react-router-dom";
import logout from "../../images/icon/logout.svg";

function Navigation(props) {
  const [authState, setAuthState] = React.useState(false); // временное решение для кнопеи авторизоваться
  let location = useLocation();
  const history = useHistory();
  const turnToMain = () => {
    history.push("/");
  };
  const turnToSavedNews = () => {
    history.push("/saved-news");
  };
  const signinButton = () => {
    // временное решение для кнопеи авторизоваться, для просмотра сверстанных вариантов
    // history.push("/signin");
    if (!authState) {
      setAuthState(true);
    } else {
      setAuthState(false);
    }
  };

  return (
    <nav className="nav">
      <ui className="nav__links">
        <li onClick={turnToMain} className="nav__link">
          Главная
        </li>
        <li
          onClick={turnToSavedNews}
          className={`${authState ? "nav__link" : "nav__link_status_disable"}`}
        >
          Сохраненные статьи
        </li>
        <button className="nav__log-button" onClick={signinButton}>
          {" "}
          {authState ? "Грета" : "Авторизоваться"}
          <svg
            class={`${location.pathname === "/saved-news" && "nav__svg-border_state_account"}`}
            width="24"
            height="24"
            viewBox="0 0 24 24"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
            class={`"nav__svg-border" ${location.pathname === "/saved-news" && "nav__svg_state_account"}`}
            fill-rule="evenodd"
              clip-rule="evenodd"
              d="M10 6L6 6L6 18H10V20H6C4.89543 20 4 19.1046 4 18V6C4 4.89543 4.89543 4 6 4H10V6ZM17.5856 13L13.2927 17.1339L14.707 18.4958L21.4141 12.0371L14.707 5.57837L13.2927 6.9402L17.5856 11.0741H8V13H17.5856Z"
              fill="#1A1B22"
            />
          </svg>
        </button>
      </ui>
    </nav>
  );
}

export default Navigation;
