import React from "react";
import { useLocation, useHistory } from "react-router-dom";
import logout from "../../images/icon/logout.svg";

function Navigation(props) {
  const [authState, setAuthState] = React.useState(false); // временное решение для кнопеи авторизоваться
  let location = useLocation();
  const history = useHistory();
  const turnToMain = () => {
    history.push("/");
  };
  const turnToSavedNews = () => {
    history.push("/saved-news");
  };
  const signinButton = () => { // временное решение для кнопеи авторизоваться, для просмотра сверстанных вариантов
    // history.push("/signin");
    if (!authState) {
      setAuthState(true);
    } else {
        setAuthState(false);
    }
  };

  return (
    <nav className="nav">
      <ui className="nav__links">
        <li onClick={turnToMain} className="nav__link">
          Главная
        </li>
        <li
          onClick={turnToSavedNews}
          className={`${
            authState ? "nav__link" : "nav__link_status_disable"
          }`}
        >
          Сохраненные статьи
        </li>
        <button className="nav__log-button" onClick={signinButton}>
          {" "}
          {authState ? "Грета" : "Авторизоваться"}
        </button>
      </ui>
    </nav>
  );
}

export default Navigation;
