import React from "react";
import { useLocation, useHistory } from "react-router-dom";
import Navigation from "../Navigation/Navigation";
import Header from "../Header/Header";



function PopupMenu(props) {
    let location = useLocation();
    const history = useHistory();

    const turnToMain = () => {
        history.push("/");
      };
      const turnToSavedNews = () => {
        history.push("/saved-news");
      };

      const signinButton = () => {
        // временное решение для кнопеи авторизоваться, для просмотра сверстанных вариантов
        // history.push("/signin");
          props.onOpenLogin(true);
        //   props.onOpenPopupMenu(false);
        props.onClose();
      };


    return (
        <section className={`popup-menu ${props.isOpenPopupMenu && 'popup-menu_state_opened'}`}>
             <button type="button" className="popup-menu__close-icon" onClick={props.onClose}></button>
             {/* <Navigation loggedIn={props.loggedIn} onOpenLogin={props.onOpenLogin} /> */}


            <div className="popup-menu__wrapper">
            <h1 className="header__title" onClick={turnToMain}>
             NewsExplorer
            </h1>
            <button className="nav__log-button" onClick={signinButton}>Авторизоваться</button>

            </div>

        </section>

  );
  }

  export default PopupMenu;
